    <a href=\"{{ path('lesson') }}\" class=\"btn btn-warning\">Retour</a>
    <a href=\"{{ path('comment', {'slugLesson': form.vars.data.lesson.slug}) }}\" class=\"btn btn-info\">Voir les commentaires</a>
